    async def buy(self,tokenAddress,snipeBNBAmount):

        tokenAddress = self.web3.to_checksum_address(tokenAddress)
        contract = self.web3.eth.contract(address=self.pancake_router, abi=self.pan_cake_abi)
        tokenContract = self.web3.eth.contract(address=tokenAddress, abi=self.token_abi)
        decimals = tokenContract.functions.decimals().call()
        DECIMAL = 10 ** decimals
        tokenName = tokenContract.functions.name().call()
        balance = tokenContract.functions.balanceOf(self.myWalletAddress).call()
        token_balance = balance / 10 ** decimals
        tokenSymbol = tokenContract.functions.symbol().call()

        if token_balance == 0:
            print(f"\nTrying to buy....., {tokenSymbol}")

            tokenToBuy = self.web3.to_checksum_address(tokenAddress)
            myWalletAddress= self.web3.to_checksum_address(self.myWalletAddress)
            spend = self.web3.to_checksum_address("0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c")
            nonce = self.web3.eth.get_transaction_count(myWalletAddress)
            bnb_amount = self.web3.to_wei(snipeBNBAmount, 'ether')

            pancake_tx = contract.functions.swapExactETHForTokensSupportingFeeOnTransferTokens(
                0, [spend, tokenToBuy], myWalletAddress, (int(time.time()) + 10000)
            ).build_transaction(
                {
                    "from": myWalletAddress,
                    "value": bnb_amount,
                    "gas": 300000,
                    "gasPrice": self.web3.eth.gas_price,
                    "nonce": nonce,
                }
            )
            private_key=os.getenv("PRIVATE_KEY")

            try:
                signed_txn = self.web3.eth.account.sign_transaction( pancake_tx, private_key)
                # print(signed_txn.rawTransaction)
                print(style.GREEN + "Transaction Sent")
                tx_token = self.web3.eth.send_raw_transaction(signed_txn.rawTransaction)
                # print(tx_token)# BUY THE TOKEN
                txHash = self.web3.to_hex(tx_token)
                # print(txHash)
                tx_receipt = self.web3.eth.wait_for_transaction_receipt(txHash, timeout=20)

                if tx_receipt.status == 1:
                    print(
                        style.GREEN + " Successfully Bought " + tokenSymbol + " for " + style.GREEN + " BNB - TX ID: ",
                        'https://www.bscscan.com/tx/' + txHash)
                else:
                    print(
                        style.RED + " Sorry Transaction Failed" + tokenSymbol + " for " + style.GREEN + " BNB - TX ID: ",
                        'https://www.bscscan.com/tx/' + txHash)
            except Exception as e:
                print(style.RED + " Transaction failed.",e)
        else:
            print("You Already Own this token")


    async def sell(self,tokenAddress):
        tokenAddress = self.web3.to_checksum_address(tokenAddress)
        contract = self.web3.eth.contract(address=self.pancake_router, abi=self.pan_cake_abi)
        tokenContract = self.web3.eth.contract(address=tokenAddress, abi=self.sellAbi)
        decimals = tokenContract.functions.decimals().call()
        DECIMAL = 10 ** decimals
        tokenName = tokenContract.functions.name().call()
