            nonce = self.web3.eth.get_transaction_count(myWalletAddress)
            approve = tokenContract.functions.approve(self.pancake_router, balance).build_transaction({
                'from': myWalletAddress,
                'gasPrice': self.web3.eth.gas_price,
                'nonce': nonce
            })

            private_key =os.getenv("PRIVATE_KEY")


            try:
                signed_txn = self.web3.eth.account.sign_transaction(approve, private_key=private_key)
                tx_token = self.web3.eth.send_raw_transaction(signed_txn.rawTransaction)
                print(style.GREEN + "Approved: " + self.web3.to_hex(tx_token))
            except:
                print(style.YELLOW + "Already been approved")
                pass

            time.sleep(5)

            # Wait after approve 5 seconds before sending transaction
